    $fullName = "System Administrator";
    $role = "admin";
    
    $sql = "INSERT INTO users (username, password, email, full_name, role) VALUES (?, ?, ?, ?, ?)";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("sssss", $username, $password, $email, $fullName, $role);
    
    if ($stmt->execute()) {
        echo "<div class='success'>Default admin user created successfully!<br>Username: admin<br>Password: admin123</div>";
