$stmt->bind_param("i", $userId);
$stmt->execute();
$result = $stmt->get_result();
if ($row = $result->fetch_assoc()) {
    $unreadCount = $row['count'];
}
$stmt->close();

// Mark message as read
if (isset($_GET['read']) && is_numeric($_GET['read'])) {
