// Check if messages table exists
$messagesTableExists = $conn->query("SHOW TABLES LIKE 'messages'")->num_rows > 0;

if (!$messagesTableExists) {
    // Create messages table without foreign keys first
    $sql = "CREATE TABLE messages (
        id INT AUTO_INCREMENT PRIMARY KEY,
        sender_id INT NOT NULL,
        receiver_id INT DEFAULT NULL,
        subject VARCHAR(255) NOT NULL,
        message TEXT NOT NULL,
        is_read TINYINT(1) DEFAULT 0,
        parent_id INT DEFAULT NULL,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
    )";
    
    if ($conn->query($sql)) {
        echo "<div class='success'>Messages table created successfully!</div>";
        
        // Try to add foreign keys one by one
        try {
            $conn->query("ALTER TABLE messages ADD CONSTRAINT fk_sender_id FOREIGN KEY (sender_id) REFERENCES users(id) ON DELETE CASCADE");
            echo "<div class='success'>Added sender_id foreign key.</div>";
        } catch (Exception $e) {
            echo "<div class='warning'>Could not add sender_id foreign key: " . $e->getMessage() . "</div>";
        }
        
        try {
            $conn->query("ALTER TABLE messages ADD CONSTRAINT fk_receiver_id FOREIGN KEY (receiver_id) REFERENCES users(id) ON DELETE SET NULL");
            echo "<div class='success'>Added receiver_id foreign key.</div>";
        } catch (Exception $e) {
            echo "<div class='warning'>Could not add receiver_id foreign key: " . $e->getMessage() . "</div>";
        }
    } else {
        echo "<div class='error'>Error creating messages table: " . $conn->error . "</div>";
    }
} else {
    echo "<div class='success'>Messages table already exists.</div>";
}

// Check if we need to add admin user for messaging
