    LEFT JOIN users s ON m.sender_id = s.id
    LEFT JOIN users r ON m.receiver_id = r.id
    WHERE m.sender_id = ? OR m.receiver_id = ?
    ORDER BY m.created_at DESC
";

$stmt = $conn->prepare($query);
$stmt->bind_param("ii", $userId, $userId);
