if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['send_message'])) {
    $receiverId = isset($_POST['receiver_id']) ? (int)$_POST['receiver_id'] : null;
    $subject = trim($_POST['subject']);
