    $email = "admin@walizoneautotech.com";
    $fullName = "System Administrator";
    $role = "admin";
    
    $sql = "INSERT INTO users (username, password, email, full_name, role) VALUES (?, ?, ?, ?, ?)";
    $stmt = $conn->prepare($sql);
