    $result = $stmt->get_result();
    
    if ($result->num_rows > 0) {
        $conn->query("UPDATE messages SET is_read = 1 WHERE id = $messageId");
    }
    $stmt->close();
}

// Get message details if viewing a specific message
$viewMessage = null;
if (isset($_GET['view']) && is_numeric($_GET['view'])) {
