    if (empty($subject) || empty($messageContent)) {
        $message = 'Please fill in all required fields.';
        $messageType = 'error';
    } else {
        $stmt = $conn->prepare("INSERT INTO messages (sender_id, receiver_id, subject, message) VALUES (?, ?, ?, ?)");
